import React, { useState } from 'react';
import useWeather from '../hooks/useWeather';

const Weather = () => {
  const [query, setQuery] = useState('');
  const [weatherData, isLoading, error] = useWeather(query);

  const handleSubmit = (event) => {
    event.preventDefault();
    // call API to fetch weather data for the entered location
  };

  return (
    <div>
      <h2>Weather Forecast</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={query}
          onChange={(event) => setQuery(event.target.value)}
          placeholder="Enter a location"
        />
        <button type="submit">Search</button>
      </form>
      {isLoading && <p>Loading...</p>}
      {error && <p>There was an error fetching weather data.</p>}
      {weatherData && (
        <div>
          <h3>{weatherData.city.name}</h3>
          <ul>
            {weatherData.list.map((day) => (
              <li key={day.dt}>{day.temp.day}Â°F</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Weather;
